mapper.update:
update blacklist set blacklist_local = #{blacklistLocal}, blacklist_title= #{blacklistTitle}, 
blacklist_content=#{blacklistContent}, blackuser_code=#{blackuserCode}
where blacklist_code = #{blacklistCode} and user_code = #{userCode}

mapper.select:
select comment_code, user_name, content, user_code, date_format(bcoment_create_date, '%Y-%m-%d %H:%i') bcoment_create_date,
from blacklist_comment
where comment_code = #{commentCode}
