mapper.select:
select count(*)
from gamelist
where game_type = #{gameType}

mapper.update:
update gamelist 
set game_day = #{gameDay} ,game_time = #{gameTime} , level = #{level} , close = #{close},game_minp = #{gameMinp},game_maxp = #{gameMaxp},game_pnum = #{gamePnum},game_mag = #{gameMag}
where game_code = #{gameCode}
