mapper.select:
select game_code,game_type,game_name,DATE_FORMAT(game_day,'%Y-%m-%d') AS game_day,game_gender,
DATE_FORMAT(game_time, '%H') AS game_time,level,
game_macth,game_place,close,game_pnum,game_mag,field_name,
field_address,game_minp,game_maxp
from gamelist

	
		and DATE_FORMAT(game_day, '%Y-%m-%d') = #{gameDay}
	
	
		and game_place = #{gamePlace}
	
	
		and close = #{close}
	
	
		and game_macth = #{gameMacth}
	
	
		and game_type = #{gameType}
	

group by game_name order by game_time

select DATE_FORMAT(game_time, '%H') AS game_time from gamelist
	
		
			and DATE_FORMAT(game_day, '%Y-%m-%d') = #{gameDay}
		
		
			and game_name = #{gameName}
